package com.kodnest.day;

public class Session2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0, b = 0;
		long k = (long) 42.47;
		System.out.println(a);
		System.out.println(b + k);
		System.out.println(b++);
		System.out.println(++a);
		a = a++ + ++a + a++;
		b = a;
		System.out.println(b);

	}

}
